function Modal(props){

    function cancelHendler(){props.onCancel()}
    function confirmHendler(){props.onConfirm()}

    return <div className={classes.modal}>
        <p>Are you shure</p>
        <button className= {classes.btnalt} onClick={props.cancelClose}>Уехать</button>
        <button className={classes.btn} onClick={props.confirmClose} >Хвать!</button>
    </div>
}
export default Modal
