import { useContext } from 'react';

import Card from '../ui/Card';
import classes from './MeetupItem.module.css';
import FavoritesContext from '../../store/favorites-context';


function MeetupItem(props) {
  const favoritesCtx = useContext(FavoritesContext);

  const itemIsFavorite = favoritesCtx.itemIsFavorite(props.id);

  function toggleFavoriteStatusHandler() {
    if (itemIsFavorite) {
      favoritesCtx.removeFavorite(props.id);
    } else {
      favoritesCtx.addFavorite({
        id: props.id,
        title: props.title,
        description: props.description,
        image: props.image,
        address: props.address,
      });
    }
  }

  return (
    <li className={classes.item}>
      <Card>
        <div className={classes.image}>
          <img src={props.image} alt={props.title} />
        </div>
        <div className={classes.content}>
          <h3>{props.title}</h3>
          <address>{props.address}</address>
          <p>{props.description}</p>
        </div>
        <div className={classes.actions}>
          <button onClick={toggleFavoriteStatusHandler}>
            <span>{itemIsFavorite ? 'Ладно, свободен' : 'Птичку в клетку!'}</span>
            {itemIsFavorite ? 'Ладно, свободен' : <img className={classes.maidodyr} src='https://firebasestorage.googleapis.com/v0/b/react-training-ca974.appspot.com/o/source%2Flol.webp?alt=media&token=abba221f-3b10-492e-b154-44e1fa73efb8'/>}
          </button>
        </div>
      </Card>
    </li>
  );
}

export default MeetupItem;
