import classes from "./Modal.module.css"

function Modal(props){

    function cancelHendler(){props.onCancel()}
    function confirmHendler(){props.onConfirm()}

    return <div className={classes.modal}>
        <p>Are you shure</p>
        <button className= {classes.btnalt} onClick={props.cancelClose}>Cancel</button>
        <button className={classes.btn} onClick={props.confirmClose} >Confirm</button>
    </div>
}
export default Modal
